msg2.headers["Content-Type"] = "application/json";


msg2.payload = {
  "text": msg.payload,
  "features": {
    "entities": {
      "emotion": true,
      "sentiment": true,
      "limit": 5
    },
    "keywords": {
      "emotion": true,
      "sentiment": true,
      "limit": 5
    },
    "sentiment": {
      "emotion": true,
      "sentiment": true,
      "limit": 5
    }
  }
}

return msg2;

flow.set("cTime", Date.now());
var a = flow.get("cSentiment");
a.push(msg.payload.sentiment.document.score*100);
flow.set("cSentiment", a);
msg.turn = true;
msg.payload = "sudo -u postgres -H -- psql -d postgres -c 'INSERT INTO public.\"SENTIMENT\"(\"USER\", \"ETIME\", \"SCORE\") VALUES (111, CURRENT_TIMESTAMP, " + msg.payload.sentiment.document.score*100 + ");'";

return msg;

msg.payload.intents.forEach(function(entry) {
    var main_intent = entry.intent.split("-")[0];
    var sub_intent = entry.intent.split("-")[1];

    flow.set("cTime", Date.now());
    msg.turn = true;
    var a = flow.get("cIntent");
    a.push([main_intent, entry.confidence*100]);
    flow.set("cIntent", a);

    var filterOut= ["undefined", "Bot_Control", "General_Conversation", "Agent_Capabilities"];
    if(main_intent && !filterOut.includes(main_intent)){
        msg.payload = "sudo -u postgres -H -- psql -d postgres -c 'INSERT INTO public.\"CALL_CLASSIFICATION\"(\"USER\", \"CLASS_NAME\", \"CONFIDENCE\") VALUES (111, $$" + main_intent + "$$, " + entry.confidence*100 + ");'";
        node.send(msg);
        if(sub_intent && !filterOut.includes(sub_intent)){
            msg.payload = "sudo -u postgres -H -- psql -d postgres -c 'INSERT INTO public.\"CALL_CLASSIFICATION_FINE\"(\"USER\", \"CLASS_NAME\", \"CONFIDENCE\") VALUES (111, $$" + sub_intent + "$$, " + entry.confidence*100 + ");'";
            node.send(msg);
        }
    }
});

