var cApi = null;

function defer() {
    if (CognosApi) {
        $.get('/cSession', function(result) {
              sessionObj = result;
              cApi = new CognosApi({
                  cognosRootURL: "{{{cognosUrl}}}",
                  sessionCode: sessionObj.sessionCode,
                  node: document.getElementById('dashboard')
              });
              cApi.initialize().then(function() {
                  cApi.dashboard.openDashboard({dashboardSpec: dashSpec}).then(
                      function(dashboardAPI) {
                           cApi.dashboardAPI = dashboardAPI;
                      }
                  ).catch(
                      function(err) {
                          console.log(err);
                      }
                  );
              }, function(err) {
                  console.log('Failed to create API. ' + err.message);
              });
            });
    } else {
        setTimeout(function() { defer() }, 50);
    }
}
